import {useRef, useEffect} from 'react'


function Admin() {
    const newBuilding = useRef ()

    const hendleSubmit = (e) => {
        e.preventDefault()
        const new_building = newBuilding.current.value
        fetch(`http://localhost:8000/newBuilding`,{
            method: "POST",
            headers:{
                "Content-Type": "application/json",
                'Access-Control-Allow-Origin': '*'
            },
            body: JSON.stringify({
                name : new_building
            })
        })
        .catch(e => console.error(e))

    }



    return(
        <>
    
        <h2>Add data</h2>
        <div className="wrapper">
            <div className="building_add">
                <form onSubmit={hendleSubmit}>
                    <input type="text" name="name" ref={newBuilding} placeholder="Building name" required />
                    <button type="submit">Send</button>
                </form>
            </div>
        </div>
        </>
    )
}

export default Admin;
